mapper.select:
select
sl.*,
sl.id as log_id,
ss.system_name
from sys_log sl
left join sys_system ss on sl.system_id = ss.id

    sl.is_delete = 0

        and sl.system_id = #{dto.systemId}


        and sl.type = #{dto.logType.code}


        and sl.event = #{dto.event}


       and sl.create_user_code like concat('%', #{dto.createUserCode}, '%')


       and sl.create_time between #{dto.startCreateTime} and #{dto.endCreateTime}


        and sl.id in

            #{id}


    order by sl.create_time desc

        SELECT
            *
        FROM
            sys_log
        WHERE
            `event` IN

                #{code}

	        AND type = #{logType}
	        AND create_time BETWEEN #{startDate} and #{endDate}

select sl.id online_id, ss.system_name, sl.create_user_code username, sl.create_user_name real_name, sl.ip_address, sl.os, sl.browser, sl.create_time login_time from sys_log sl left join sys_system ss on sl.system_id = ss.id where sl.id in ( select max(id) from sys_log where is_delete = 0 and create_time > #{dto.startCreateTime} group by create_user_code ) and sl.event = #{dto.event}
and sl.system_id = #{dto.systemId}
and sl.create_user_code like concat('%', #{dto.username}, '%')
and sl.ip_address like concat('%', #{dto.ipAddress}, '%')
order by sl.create_time desc
